// src/components/ShowMap.jsx
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';

// Please be sure you have installed the package:
//   npm install @react-google-maps/api
// Also create a .env file in your project root with:
//   REACT_APP_GOOGLE_MAPS_API_KEY=YOUR_GOOGLE_MAPS_API_KEY

const MAP_CONTAINER_STYLE = {
  width: '100%',
  height: 'calc(100vh - 4rem)', 
};

const DEFAULT_CENTER = { lat: 20, lng: 0 };
const DEFAULT_ZOOM = 2;

export default function ShowMap({ token, logout, countries }) {
  const navigate = useNavigate();
  const [mapCenter, setMapCenter] = useState(DEFAULT_CENTER);

  // Load the Google Maps JS API key from .env
  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }
    // If there are fetched countries with valid latlng, center on the first one
    if (countries.length > 0 && Array.isArray(countries[0].latlng)) {
      setMapCenter({ lat: countries[0].latlng[0], lng: countries[0].latlng[1] });
    }
  }, [token, navigate, countries]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleBack = () => {
    navigate('/solution');
  };

  if (loadError) {
    return (
      <div className="relative min-h-screen bg-gray-100 flex items-center justify-center">
        <p>Error loading Google Maps</p>
      </div>
    );
  }

  if (!isLoaded) {
    // While the Maps JS API is loading, show a placeholder
    return (
      <div className="relative min-h-screen bg-gray-100 flex items-center justify-center">
        <p>Loading map...</p>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackToHomeButton />
          <button
            onClick={handleBack}
            className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-300 transition-colors"
          >
            ‚Üê
          </button>
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer so map sits below navbar */}
      <div className="pt-16" />

      {/* Google Map with markers */}
      <GoogleMap
        mapContainerStyle={MAP_CONTAINER_STYLE}
        center={mapCenter}
        zoom={DEFAULT_ZOOM}
      >
        {countries.map((country) => {
          if (
            Array.isArray(country.latlng) &&
            country.latlng.length === 2 &&
            typeof country.latlng[0] === 'number' &&
            typeof country.latlng[1] === 'number'
          ) {
            return (
              <Marker
                key={country.code}
                position={{
                  lat: country.latlng[0],
                  lng: country.latlng[1],
                }}
                title={country.name}
                animation={window.google.maps.Animation.DROP}
              />
            );
          }
          return null;
        })}
      </GoogleMap>
    </div>
  );
}
