import { useState, useEffect} from 'react';
import { doRequest } from '../utils/api';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [task, setTask] = useState(null);
  const [message, setMessage] = useState({type:'', text:''});

  useEffect(() => {
    if (!message)
      return;

    const onInteraction = () => {
      setMessage({type:'', text:''});
    };
    window.addEventListener('keydown', onInteraction);
    window.addEventListener('pointerdown', onInteraction);

    return () => {
      window.removeEventListener('keydown', onInteraction);
      window.removeEventListener('pointerdown', onInteraction);
    };
  }, [message.text]);

  async function register() {
    /*
    setMessage({type:'', text:''});

    try {
      const response = await fetch('https://bootcamp2025.depster.me/registration', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();

    if(!response.ok) {
      const msg = data.message || 'An error occurred during registration.';
      setMessage({type:'error', text:msg});
      return;
    }
    setMessage({type:'success', text:'Successful registration! You can log in now.'});
    }
    catch(err) {
      //console.error(err);
      setMessage({type:'error', text:'Error in communication with the server.'});
    }*/
   const data = await doRequest({
    url: 'https://bootcamp2025.depster.me/registration',
    method: 'POST',
    body: {email, password},
    token: '',
    setMessage,
   })
  }

  async function login() {
    setMessage({type:'', text:''});

    try {
      const response = await fetch('https://bootcamp2025.depster.me/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();

    if(!response.ok) {
      const msg = data.message || 'Login failed. Please check your data.';
      setMessage({type:'error', text:msg});
      return;
    }
    setToken(data.data.token);
    setMessage({type:'success', text:'Successful login!'});
    }
    catch(err) {
      //console.error(err);
      setMessage({type:'error', text:'Error in communication with the server.'});
    }
  }

  async function getTask() {
    setMessage({type:'', text:''});

    try {
      const response = await fetch('https://bootcamp2025.depster.me/task', {
      method: 'GET',
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();

    if(!response.ok) {
      const msg = data.message || 'Error retrieving tasks.';
      setMessage({type:'error', text:msg});
      return;
    }
    setTask(data);
    setMessage({type:'success', text:'Task is successfully retrieved!'});
    }
    catch (err) {
      console.error(err);
      setMessage({type:'error', text:'Error in communication with the server.'});
    }
  }

  return (
    <div className="min-h-screen bg-gray-100">

      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
          Dept Bootcamp App
        </h1>

        {message.text && (
          <div className={message.type === 'error' ? "error-message" : "success-message"}>
            {message.text}
          </div>
        )}

        {!token && (
        <div className="flex flex-col sm:flex-row gap-4 mb-6">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input-field"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input-field"
          />
        </div>)}

        <div className="flex gap-4">
          {!token && (
            <>
              <button onClick={register} className="btn">
              Register
              </button>
              <button onClick={login} className="btn">
              Login
              </button>
          </>)}

          {token && (
            <>
              <button onClick={getTask} className="btn">
                Show Task
              </button>
              <button className="btn">
                Show Solution
              </button>
            </>
          )}
        </div>
      </div>

      {task && (
        <div className="p-6 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Task:</h2>
          <pre className="bg-white p-4 rounded shadow">
            {JSON.stringify(task, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;

