import { useState, useEffect} from 'react';
import { doRequest } from './utils/api';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [task, setTask] = useState(null);
  const [message, setMessage] = useState({type:'', text:''});

  useEffect(() => {
    if (!message)
      return;

    const onInteraction = () => {
      setMessage({type:'', text:''});
    };
    window.addEventListener('keydown', onInteraction);
    window.addEventListener('pointerdown', onInteraction);

    return () => {
      window.removeEventListener('keydown', onInteraction);
      window.removeEventListener('pointerdown', onInteraction);
    };
  }, [message.text]);

  async function register() {
   const data = await doRequest({
    url: 'https://bootcamp2025.depster.me/registration',
    method: 'POST',
    body: {email, password},
    token: '',
    setMessage,
   });
   if (data) {
    setMessage({type:'success', text:'Successful registration! You can log in now.'});
   }
  }

  async function login() {
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/login',
      method: 'POST',
      body: {email, password},
      token: '',
      setMessage,
    });
    if(data && data.data && data.data.token) {
      setToken(data.data.token);
      setMessage({type:'success', text:'Successful login!'});
    }
  }

  async function getTask() {
   const data = await doRequest({
    url: 'https://bootcamp2025.depster.me/task',
    method: 'GET',
    body: null,
    token,
    setMessage,
   });
   if(data) {
    setTask(data);
    setMessage({type:'success', text:'Task is successfully retrieved!'});
   }
  }

  function logout() {
    setToken('');
    setTask(null);

  }

  return (
    <div className="min-h-screen bg-gray-100">

      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
          Dept Bootcamp App
        </h1>

        {message.text && (
          <div className={message.type === 'error' ? "error-message" : "success-message"}>
            {message.text}
          </div>
        )}

        {!token && (
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="input-field"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="input-field"
            />
          </div>
      )}

      <div className="flex gap-4">
        {!token && (
          <>
            <button onClick={register} className="btn">
              Register
            </button>
            <button onClick={login} className="btn">
              Login
            </button>
          </>
        )}
        {token && (
          <>
            <button onClick={getTask} className="btn">
              Show Task
            </button>
            <button className="btn">
              Show Solution
            </button>
          </>
          )}
        </div>
      </div>

      {task && (
        <div className="p-6 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Task:</h2>
          <pre className="bg-white p-4 rounded shadow">
            {JSON.stringify(task, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;

