import { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [task, setTask] = useState(null);

  async function register() {
    const response = await fetch('https://bootcamp2025.depster.me/registration', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();
    console.log(data);
    alert('Registration complete! Now log in.');
  }

  async function login() {
    const response = await fetch('https://bootcamp2025.depster.me/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();
    setToken(data.data.token);
    console.log(data);
    alert('Login successful!');
  }

  async function getTask() {
    const response = await fetch('https://bootcamp2025.depster.me/task', {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    const data = await response.json();
    setTask(data);
    console.log(data);
  }

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-6xl font-bold text-gray-800 mb-4">Dept Bootcamp App</h1>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="bg-gray-200 border border-gray-300 rounded px-2 py-1 mr-2 hover:bg-white"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="bg-gray-200 border border-gray-300 rounded px-2 py-1 mr-2 hover:bg-white"
      />
      <div style={{ marginTop: 10 }}>
        <button onClick={register} className="btn">Register</button>
        <button onClick={login} className="btn">Login</button>
        { token && (<button onClick={getTask} className="btn" disabled={!token}>Get Task</button>)}
        { token && (<button className="btn" disabled={!token}>Task solution</button>)}
      </div>
      {task && (
        <div style={{ marginTop: 20 }}>
          <h2>Task:</h2>
          <pre>{JSON.stringify(task, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
