// src/App.jsx
import { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { doRequest } from './utils/api';

import HomePage from './components/HomePage';
import TaskPage from './components/TaskPage';
import SolutionPage from './components/SolutionPage';
import SavedCountries from './components/SavedCountries';
import ShowMap from './components/ShowMap';
import CountryDetailPage from './components/CountryDetailPage';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [task, setTask] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });

  // Lifted state for fetched countries so it persists across navigation
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    if (!message.text) return;

    const onInteraction = () => {
      setMessage({ type: '', text: '' });
    };
    window.addEventListener('keydown', onInteraction);
    window.addEventListener('pointerdown', onInteraction);

    return () => {
      window.removeEventListener('keydown', onInteraction);
      window.removeEventListener('pointerdown', onInteraction);
    };
  }, [message.text]);

  async function register() {
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/registration',
      method: 'POST',
      body: { email, password },
      token: '',
      setMessage,
    });
    if (data) {
      setMessage({ type: 'success', text: 'Successful registration! You can log in now.' });
    }
  }

  async function login() {
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/login',
      method: 'POST',
      body: { email, password },
      token: '',
      setMessage,
    });
    if (data && data.data && data.data.token) {
      setToken(data.data.token);
      setMessage({ type: 'success', text: 'Successful login!' });
    }
  }

  async function getTask() {
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/task',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      setTask(data);
      setMessage({ type: 'success', text: 'Task is successfully retrieved!' });
    }
  }

  function logout() {
    setToken('');
    setTask(null);
    setMessage({ type: '', text: '' });
    setCountries([]); // optional: clear fetched countries on logout
    // NOTE: do NOT clear localStorage here, so savedCountries persist across sessions
  }

  return (
    <BrowserRouter>
      <div className="min-h-screen bg-gray-100">
        <Routes>
          <Route
            path="/"
            element={
              <HomePage
                email={email}
                setEmail={setEmail}
                password={password}
                setPassword={setPassword}
                register={register}
                login={login}
                message={message}
                token={token}
                logout={logout}
              />
            }
          />
          <Route
            path="/task"
            element={
              <TaskPage
                token={token}
                task={task}
                getTask={getTask}
                message={message}
                logout={logout}
              />
            }
          />
          <Route
            path="/solution"
            element={
              <SolutionPage
                token={token}
                message={message}
                setMessage={setMessage}
                logout={logout}
                countries={countries}
                setCountries={setCountries}
              />
            }
          />
          <Route
            path="/saved"
            element={
              <SavedCountries
                token={token}
                logout={logout}
              />
            }
          />
          <Route
            path="/show-map"
            element={
              <ShowMap
                token={token}
                logout={logout}
              />
            }
          />
          <Route 
            path="/country/:code" 
            element={
              <CountryDetailPage
               token={token}
               logout={logout}
                />} 
/>
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
