// src/components/TaskPage.jsx
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function TaskPage({ token, task, getTask, message, logout }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="relative flex flex-col items-center justify-center min-h-screen px-4">
        <img
         src={logo}
         alt="Dept Logo"
         className="absolute top-4 left-4 w-20 h-auto"
        />
      {token && (
        <button
          onClick={handleLogout}
          className="btn absolute top-4 right-4"
        >
          Log out
        </button>
      )}

      <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
        Dept Bootcamp App
      </h1>

      {message.text && (
        <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
          {message.text}
        </div>
      )}

      <button onClick={getTask} className="btn mb-6">
        Show Task
      </button>

      {task && (
        <div className="p-6 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Task:</h2>
          <pre className="bg-white p-4 rounded shadow">
            {JSON.stringify(task, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}
