// src/components/SavedCountries.jsx
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';
import CountryCard from './CountryCard';

export default function SavedCountries({ token, logout }) {
  const navigate = useNavigate();
  const [saved, setSaved] = useState([]);

  // 1) On mount: redirect if no token, otherwise load saved from localStorage
  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }
    const stored = JSON.parse(localStorage.getItem('savedCountries') || '[]');
    setSaved(stored);
  }, [token, navigate]);

  // 2) Remove a country and update localStorage
  const removeCountry = (code) => {
    const updated = saved.filter((c) => c.code !== code);
    setSaved(updated);
    localStorage.setItem('savedCountries', JSON.stringify(updated));
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleBack = () => {
    navigate('/solution');
  };

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-10 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <button
            onClick={handleBack}
            className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-300 transition-colors"
          >
            ‚Üê
          </button>
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">
        Saved Countries
      </h1>

      {saved.length > 0 ? (
        <div className="w-full max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8 px-2">
          {saved.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              isSaved={true}
              onRemove={removeCountry}
            />
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-600">No saved countries found.</p>
      )}
    </div>
  );
}
