// src/components/TaskPage.jsx
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function TaskPage({ token, task, getTask, message, logout }) {
  const navigate = useNavigate();

  // Redirect to home if not logged in
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // Safely extract name & description from the task object
  const name = task?.data?.name || '';
  const descriptionRaw = task?.data?.description || '';
  // Split on literal "\n" sequences into lines
  const lines = descriptionRaw.split('\\n').filter(line => line.trim() !== '');

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      {/* Show Task button */}
      <div className="w-full flex justify-center mb-6">
        <button onClick={getTask} className="btn">
          Show Task
        </button>
      </div>

      {/* Task content */}
      {task && (
        <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow mb-8">
          {/* Title */}
          <h1 className="text-3xl font-bold text-gray-800 mb-4">{name}</h1>
          {/* Description lines */}
          <div className="prose prose-lg text-gray-700">
            {lines.map((line, idx) => (
              <p key={idx}>{line.trim()}</p>
            ))}
          </div>
        </div>
      )}

      {/* Optional error/success message */}
      {message.text && (
        <div className="w-full flex justify-center mb-2">
          <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
            {message.text}
          </div>
        </div>
      )}
    </div>
  );
}
