// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState(code);
  const [photos, setPhotos] = useState([]);
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // redirect ako nije logiran
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // dohvat imena zemlje
  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const data = await res.json();
        if (Array.isArray(data) && data[0]?.name?.common) {
          setCountryName(data[0].name.common);
        }
      } catch {
        /* fallback */
      }
    })();
  }, [code]);

  // dohvat slika s Pixabay
  useEffect(() => {
    async function load() {
      setLoading(true);
      setError('');

      const key = process.env.REACT_APP_PIXABAY_KEY;
      if (!key) {
        setError('Pixabay API key not set (REACT_APP_PIXABAY_KEY).');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `https://pixabay.com/api/?key=${key}` +
            `&q=${encodeURIComponent(countryName)}` +
            `&image_type=photo&per_page=5`
        );
        if (!res.ok) {
          throw new Error(`Pixabay responded ${res.status}`);
        }
        const json = await res.json();
        if (Array.isArray(json.hits)) {
          setPhotos(json.hits.map((h) => h.webformatURL));
        } else {
          setError('Unexpected Pixabay response format.');
        }
      } catch (e) {
        console.error(e);
        setError(`Pixabay error: ${e.message}`);
      } finally {
        setLoading(false);
      }
    }

    if (countryName) {
      load();
    }
  }, [countryName]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // loading ili error
  if (loading && !error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading photos…</p>
      </div>
    );
  }
  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  const prev = () => setCurrent((c) => (c > 0 ? c - 1 : photos.length - 1));
  const next = () => setCurrent((c) => (c < photos.length - 1 ? c + 1 : 0));

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* fiksni nav bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* spacer */}
      <div className="pt-16" />

      {/* naslov */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* carousel */}
      <div className="mt-8 flex items-center justify-center space-x-4 px-4">
        {photos.length ? (
          <>
            <button onClick={prev} className="btn px-3 py-2 text-sm">
              ◀
            </button>
            <img
              src={photos[current]}
              alt={`${countryName} ${current + 1}`}
              className="w-full max-w-2xl h-64 object-cover rounded shadow"
            />
            <button onClick={next} className="btn px-3 py-2 text-sm">
              ▶
            </button>
          </>
        ) : (
          <p>No photos found for {countryName}.</p>
        )}
      </div>
    </div>
  );
}
