// src/components/CountryCard.jsx
import React from 'react';
import { FiMapPin } from 'react-icons/fi';

export default function CountryCard({ country, onSave, onRemove, isSaved }) {
  return (
    <div
      className="bg-white rounded-lg shadow overflow-hidden flex flex-col transform transition duration-200 hover:scale-105"
      style={{ minHeight: '24rem' }}
    >
      {/* Flag image */}
      <div className="w-full h-48 overflow-hidden">
        <img
          src={country.flag}
          alt={`${country.name} flag`}
          className="w-full h-full object-cover"
        />
      </div>

      {/* Country info */}
      <div className="p-4 flex flex-col flex-grow">
        <h3 className="text-lg font-semibold text-gray-800 mb-2 text-center">
          {country.name}
        </h3>
        <div className="flex justify-center items-center text-gray-600 mb-4">
          <FiMapPin className="mr-1" />
          <span>{country.capital}</span>
        </div>
        <div className="mt-auto flex justify-center">
          {!isSaved ? (
            <button
              onClick={() => onSave(country)}
              className="btn px-4 py-2 text-sm"
            >
              Save
            </button>
          ) : (
            <button
              onClick={() => onRemove(country.code)}
              className="btn bg-red-500 hover:bg-red-700 px-4 py-2 text-sm"
            >
              Remove
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
