// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams(); // dobivamo ISO kod iz URL-a
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Ako nije logiran, vrati na /
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // 2) Dohvati puni naziv države
  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const js = await res.json();
        setCountryName(js[0]?.name?.common || code); // postavljamo ime zemlje
      } catch {
        setCountryName(code); // ako nešto pođe po zlu, prikazujemo samo kod
      }
      setLoading(false);
    })();
  }, [code]);

  // 3) Ako je došlo do pogreške
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading country details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  // 4) Glavni render
  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fiksni navbar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={logout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      {/* Naslov države */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* Google mapa */}
      <div className="mt-8 flex justify-center">
        <iframe
          title={`map-${code}`}
          src={`https://maps.google.com/maps?q=${encodeURIComponent(
            countryName
          )}&output=embed`}
          className="w-full max-w-2xl h-64 object-cover rounded shadow"
          allowFullScreen
        ></iframe>
      </div>
    </div>
  );
}
