// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [country, setCountry] = useState({
    name: '',
    callingCodes: '',
    region: '',
    subregion: '',
    population: null,
    demonym: '',
    area: null,
    currencies: [],
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [open, setOpen] = useState(false);

  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        if (!res.ok) throw new Error(res.statusText);
        const js = await res.json();
        const info = js[0] || {};

        let calling = '–';
        if (info.idd?.root && Array.isArray(info.idd.suffixes)) {
          calling = info.idd.suffixes.map(s => info.idd.root + s).join(', ');
        }

        const currs = info.currencies || {};
        const currencies = Object.entries(currs).map(([c, cur]) => `${c} (${cur.name})`);

        let dem = '–';
        if (info.demonyms?.eng?.m) dem = info.demonyms.eng.m;

        setCountry({
          name: info.name?.common || code,
          callingCodes: calling,
          region: info.region || '–',
          subregion: info.subregion || '–',
          population: typeof info.population === 'number' ? info.population : null,
          demonym: dem,
          area: typeof info.area === 'number' ? info.area : null,
          currencies,
        });
      } catch (e) {
        console.error(e);
        setError('Error fetching country data.');
      } finally {
        setLoading(false);
      }
    })();
  }, [code]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading country details…</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed navbar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={logout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      {/* Country Name */}
      <h1 className="text-6xl font-bold text-gray-800 text-center mt-6">
        {country.name}
      </h1>

      {/* Square map */}
      <div className="mt-4 flex justify-center px-4">
        <div className="w-3/4 max-w-3xl aspect-square rounded overflow-hidden shadow">
          <iframe
            title={`map-${code}`}
            src={`https://maps.google.com/maps?q=${encodeURIComponent(
              country.name
            )}&output=embed`}
            className="w-full h-full object-cover"
            allowFullScreen
          />
        </div>
      </div>

      {/* Accordion for details */}
      <div className="mt-6 max-w-3xl mx-auto px-4">
        <button
          onClick={() => setOpen(!open)}
          className="w-full text-left font-semibold text-gray-800 py-2 border-b"
        >
          {open ? 'Hide Details ▲' : 'Show Details ▼'}
        </button>
        {open && (
          <div className="pt-4 text-left text-gray-700 space-y-2">
            <p><strong>Calling Codes:</strong> {country.callingCodes}</p>
            <p><strong>Region:</strong> {country.region}</p>
            <p><strong>Subregion:</strong> {country.subregion}</p>
            <p><strong>Population:</strong> {country.population !== null ? country.population.toLocaleString('hr-HR') : '–'}</p>
            <p><strong>Demonym:</strong> {country.demonym}</p>
            <p><strong>Area:</strong> {country.area !== null ? `${country.area.toLocaleString('hr-HR')} km²` : '–'}</p>
            <p><strong>Currencies:</strong> {country.currencies.join(', ') || '–'}</p>
          </div>
        )}
      </div>
    </div>
  );
}
