import React, { useState } from 'react';

export default function Random({ limit = 5 }) {
  const [data, setData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchRandom = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(`https://bootcamp2025.depster.me/api/countries?limit=${limit}`);
      if (!res.ok) throw new Error(`Status ${res.status}`);
      const json = await res.json();
      setData(json);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-4 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Random Countries (limit: {limit})</h2>
      <button
        onClick={fetchRandom}
        className="btn mb-4"
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Fetch Random'}
      </button>
      {error && <p className="text-red-600 mb-2">Error: {error}</p>}
      {data && (
        <pre className="bg-gray-100 p-4 rounded overflow-auto text-sm">
          {JSON.stringify(data, null, 2)}
        </pre>
      )}
    </div>
  );
}
