// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState(code);
  const [loading, setLoading] = useState(true);

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  // Fetch full country name by ISO code
  useEffect(() => {
    async function fetchName() {
      try {
        const res = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const data = await res.json();
        if (Array.isArray(data) && data[0]?.name?.common) {
          setCountryName(data[0].name.common);
        }
      } catch {
        // ignore, fallback to code
      } finally {
        setLoading(false);
      }
    }
    fetchName();
  }, [code]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      {/* Country Name (double size) */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* Embedded Google Map */}
      <div className="mt-5 w-full flex justify-center px-4">
        <iframe
          title={`map-${countryName}`}
          src={`https://maps.google.com/maps?q=${encodeURIComponent(countryName)}&output=embed`}
          className="w-full max-w-4xl h-96 rounded shadow"
          allowFullScreen
        />
      </div>
    </div>
  );
}
