// src/components/ShowMap.jsx
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../images/dept-logo.png';

// Leaflet imports (install via: npm install leaflet react-leaflet)
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

export default function ShowMap({ token, logout, countries }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleBack = () => {
    navigate('/solution');
  };

  // Determine initial center: if at least one country fetched, center on first; otherwise [20,0].
  const initialCenter =
    countries.length > 0 && Array.isArray(countries[0].latlng)
      ? [countries[0].latlng[0], countries[0].latlng[1]]
      : [20, 0];

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          {/* Single Back Arrow */}
          <button
            onClick={handleBack}
            className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-300 transition-colors"
          >
            ←
          </button>
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer so map sits below navbar */}
      <div className="pt-16" />

      {/* Leaflet Map Container */}
      <MapContainer
        center={initialCenter}
        zoom={2}
        scrollWheelZoom={true}
        style={{ height: 'calc(100vh - 4rem)', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="© OpenStreetMap contributors"
        />
        {countries.map((country) => {
          if (
            Array.isArray(country.latlng) &&
            country.latlng.length === 2 &&
            typeof country.latlng[0] === 'number' &&
            typeof country.latlng[1] === 'number'
          ) {
            return (
              <Marker
                key={country.code}
                position={[country.latlng[0], country.latlng[1]]}
              >
                <Popup>
                  <strong>{country.name}</strong>
                  <br />
                  Capital: {country.capital}
                </Popup>
              </Marker>
            );
          }
          return null;
        })}
      </MapContainer>
    </div>
  );
}
