// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState('');
  const [images, setImages] = useState([]);    // niz URL-eva
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Redirect ako nema token
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // 2) Dohvati puni naziv države
  useEffect(() => {
    (async () => {
      try {
        const r = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const js = await r.json();
        setCountryName(js[0]?.name?.common || code);
      } catch {
        setCountryName(code);
      }
    })();
  }, [code]);

  // 3) Dohvati Wikipedia lead image + još 3 random slike s iste stranice
  useEffect(() => {
    if (!countryName) return;

    (async () => {
      setLoading(true);
      setError('');

      try {
        // 3a) Lead image (pageimage)
        const wp = await fetch(
          `https://en.wikipedia.org/w/api.php?` +
          `action=query&titles=${encodeURIComponent(countryName)}` +
          `&prop=pageimages|images&format=json&origin=*&pithumbsize=800`
        ).then(r => r.json());

        const pages = wp.query?.pages;
        const page = pages && Object.values(pages)[0];
        const thumb = page?.thumbnail?.source;
        const imgs = [];

        if (thumb) imgs.push(thumb);

        // 3b) Ako ima polje images, uzmemo prvih 3 koje su jpg/png
        if (page.images) {
          for (let img of page.images) {
            if (imgs.length >= 4) break;
            const title = img.title; // npr. "File:Flag_of_France.svg"
            if (/\.(jpg|jpeg|png)$/i.test(title)) {
              // dohvatimo URL
              const info = await fetch(
                `https://en.wikipedia.org/w/api.php?` +
                `action=query&titles=${encodeURIComponent(title)}` +
                `&prop=imageinfo&iiprop=url&format=json&origin=*`
              ).then(r => r.json());
              const pg = info.query.pages;
              const itm = pg && Object.values(pg)[0];
              const url = itm.imageinfo?.[0]?.url;
              if (url) imgs.push(url);
            }
          }
        }

        if (imgs.length === 0) {
          setError('Nema slika na Wikipediji za ovu državu.');
        } else {
          setImages(imgs);
        }
      } catch (e) {
        console.error(e);
        setError('Greška pri dohvaćanju slika s Wikipedije.');
      } finally {
        setLoading(false);
      }
    })();
  }, [countryName]);

  const prev = () => setCurrent(c => (c > 0 ? c - 1 : images.length - 1));
  const next = () => setCurrent(c => (c < images.length - 1 ? c + 1 : 0));

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading && !error) {
    return <div className="min-h-screen flex items-center justify-center">Loading…</div>;
  }
  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* fiksni navbar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* spacer */}
      <div className="pt-16" />

      {/* naslov */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* carousel */}
      <div className="mt-8 flex items-center justify-center space-x-4 px-4">
        {images.length ? (
          <>
            <button onClick={prev} className="btn px-3 py-2 text-sm">
              ◀
            </button>
            <img
              src={images[current]}
              alt={`${countryName} ${current+1}`}
              className="w-full max-w-2xl h-64 object-cover rounded shadow"
            />
            <button onClick={next} className="btn px-3 py-2 text-sm">
              ▶
            </button>
          </>
        ) : (
          <p>No images to show.</p>
        )}
      </div>
    </div>
  );
}
