// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';
import CountryCard from './CountryCard';

export default function SolutionPage({
  token,
  message,
  setMessage,
  logout,
  countries,
  setCountries,
}) {
  const navigate = useNavigate();
  const [savedList, setSavedList] = useState([]);

  // 1) On mount: if no token, redirect; otherwise load savedList from localStorage
  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }
    const stored = JSON.parse(localStorage.getItem('savedCountries') || '[]');
    setSavedList(stored);
  }, [token, navigate]);

  // 2) Whenever savedList changes, persist to localStorage
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(savedList));
  }, [savedList]);

  const handleLogout = () => {
    logout();
    navigate('/');
    // Do NOT clear localStorage here; savedList should persist
  };

  // Enrich a basic country object with capital, flag, and latlng
  const enrichCountry = async (basic) => {
    let capital = 'N/A';
    let flag = '';
    let latlng = [0, 0];

    // Fetch from restcountries to get capital, latlng
    try {
      const rcResponse = await fetch(
        `https://restcountries.com/v3.1/alpha/${basic.code}`
      );
      const rcData = await rcResponse.json();
      if (Array.isArray(rcData) && rcData.length > 0) {
        const info = rcData[0];
        capital = Array.isArray(info.capital) ? info.capital[0] : info.capital || 'N/A';
        if (Array.isArray(info.latlng) && info.latlng.length === 2) {
          latlng = info.latlng;
        }
      }
    } catch {
      // ignore errors
    }

    // Fetch flag URL from countriesnow API
    try {
      const cnResponse = await fetch(
        'https://countriesnow.space/api/v0.1/countries/flag/images',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ iso2: basic.code }),
        }
      );
      const cnData = await cnResponse.json();
      if (cnData.data && typeof cnData.data.flag === 'string') {
        flag = cnData.data.flag;
      }
    } catch {
      // ignore errors
    }

    return {
      code: basic.code,
      name: basic.name,
      capital,
      flag,
      latlng,
    };
  };

  // 3) Fetch random countries (do NOT clear savedList!)
  const fetchCountries = async () => {
    setMessage({ type: '', text: '' });
    setCountries([]);

    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      const basicList = Array.isArray(data)
        ? data
        : Array.isArray(data.data)
        ? data.data
        : [];
      const enriched = await Promise.all(
        basicList.map((basic) => enrichCountry(basic))
      );
      setCountries(enriched);
      setMessage({ type: 'success', text: 'Countries fetched successfully!' });
    }
  };

  // 4) Save a country: add to savedList if not already there
  const saveCountry = (country) => {
    if (!savedList.some((c) => c.code === country.code)) {
      setSavedList((prev) => [...prev, country]);
    }
  };

  // 5) Remove a country from savedList
  const removeCountry = (code) => {
    setSavedList((prev) => prev.filter((c) => c.code !== code));
  };

  // 6) Check if a country is already saved
  const isCountrySaved = (code) => {
    return savedList.some((c) => c.code === code);
  };

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-non">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackToHomeButton />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Fixed button row below top bar */}
      <div className="fixed top-10 left-0 w-full bg-gray-100 z-10 flex items-end justify-center h-16">
        <div className="flex space-x-4 mb-2">
          <button onClick={fetchCountries} className="btn">
            Fetch Countries
          </button>
          <Link to="/saved">
            <button className="btn">Saved Countries</button>
          </Link>
          <Link to="/show-map">
            <button className="btn">Show Map</button>
          </Link>
        </div>
      </div>

      {/* Spacer so content sits below fixed bars */}
      <div className="pt-24" />

      {/* Message line */}
      <div className="w-full flex justify-center mb-2">
        {message.text && (
          <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
            {message.text}
          </div>
        )}
      </div>

      {/* Display fetched countries in cards */}
      {countries.length > 0 && (
        <div className="w-full max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8 px-2">
          {countries.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              onSave={saveCountry}
              isSaved={isCountrySaved(country.code)}
              onRemove={removeCountry}
            />
          ))}
        </div>
      )}
    </div>
  );
}
