// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState('');
  const [photos, setPhotos] = useState([]);    // Flickr URLs
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Redirect ako nema tokena
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // 2) Dohvat stvarnog imena zemlje
  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(
          `https://restcountries.com/v3.1/alpha/${code}`
        );
        const js = await res.json();
        setCountryName(
          Array.isArray(js) && js[0]?.name?.common
            ? js[0].name.common
            : code
        );
      } catch {
        setCountryName(code);
      }
    })();
  }, [code]);

  // 3) Dohvat slika s Flickr Public Feed
  useEffect(() => {
    if (!countryName) return;

    (async () => {
      setLoading(true);
      setError('');
      try {
        const url =
          'https://www.flickr.com/services/feeds/photos_public.gne' +
          '?format=json&nojsoncallback=1' +
          `&tags=${encodeURIComponent(countryName)}`;
        const res = await fetch(url);
        if (!res.ok) throw new Error(`Flickr ${res.status}`);
        const js = await res.json();
        if (Array.isArray(js.items)) {
          // uzmemo do 5 stavki
          setPhotos(
            js.items.slice(0, 5).map((it) => it.media.m)
          );
        } else {
          setError('Unexpected Flickr response');
        }
      } catch (e) {
        setError(`Flickr error: ${e.message}`);
      } finally {
        setLoading(false);
      }
    })();
  }, [countryName]);

  // 4) Carousel funkcije
  const prev = () =>
    setCurrent((c) =>
      photos.length ? (c > 0 ? c - 1 : photos.length - 1) : 0
    );
  const next = () =>
    setCurrent((c) =>
      photos.length ? (c < photos.length - 1 ? c + 1 : 0) : 0
    );

  // 5) Logout
  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // 6) Loading / error prikaz
  if (loading && !error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading photos…</p>
      </div>
    );
  }
  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  // 7) Glavni render
  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* fiksni nav bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* spacer */}
      <div className="pt-16" />

      {/* naslov */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* carousel */}
      <div className="mt-8 flex items-center justify-center space-x-4 px-4">
        {photos.length ? (
          <>
            <button onClick={prev} className="btn px-3 py-2 text-sm">
              ◀
            </button>
            <img
              src={photos[current]}
              alt={`${countryName} ${current + 1}`}
              className="w-full max-w-2xl h-64 object-cover rounded shadow"
            />
            <button onClick={next} className="btn px-3 py-2 text-sm">
              ▶
            </button>
          </>
        ) : (
          <p>No photos found for {countryName}.</p>
        )}
      </div>
    </div>
  );
}
