// src/components/CountryCard.jsx
import React, { useState } from 'react';
import { FiMapPin } from 'react-icons/fi';
import defaultFlag from '../images/flag-default.png';

export default function CountryCard({ country, onSave, onRemove, isSaved }) {
  const [showMap, setShowMap] = useState(false);

  const handleShowMap = (e) => {
    e.stopPropagation();
    setShowMap(true);
  };

  const handleGoBack = (e) => {
    e.stopPropagation();
    setShowMap(false);
  };

  // If country.flag is falsy or empty string, use defaultFlag
  const flagSrc = country.flag ? country.flag : defaultFlag;

  return (
    <div
      className="card cursor-pointer mx-2 flex flex-col"
      style={{ minHeight: '28rem' }}
    >
      {!showMap ? (
        <>
          {/* Flag image (use default if missing) */}
          <div className="w-full h-48 overflow-hidden">
            <img
              src={flagSrc}
              alt={`${country.name} flag`}
              className="w-full h-full object-cover"
            />
          </div>

          {/* Country info */}
          <div className="p-4 flex flex-col flex-grow">
            <h3 className="text-2xl font-semibold text-gray-800 mb-4 text-center">
              {country.name}
            </h3>
            <div className="flex justify-center items-center text-gray-600 mb-6">
              <FiMapPin className="mr-1" />
              <span className="text-2xl">{country.capital}</span>
            </div>
            <div className="flex-grow" />

            {/* Show map button */}
            <div className="mb-4 flex justify-center">
              <button
                onClick={handleShowMap}
                className="btn px-4 py-2 text-sm"
              >
                Show map
              </button>
            </div>

            {/* Save / Remove button */}
            <div className="flex justify-center">
              {!isSaved ? (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onSave(country);
                  }}
                  className="btn px-4 py-2 text-sm"
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onRemove(country.code);
                  }}
                  className="btn bg-red-500 hover:bg-red-700 px-4 py-2 text-sm"
                >
                  Remove
                </button>
              )}
            </div>
          </div>
        </>
      ) : (
        <div className="w-full h-full flex flex-col">
          <iframe
            title={`map-${country.code}`}
            src={`https://maps.google.com/maps?q=${encodeURIComponent(
              country.name
            )}&output=embed`}
            className="w-full h-full"
            allowFullScreen
          ></iframe>
          <div className="p-4 flex justify-center">
            <button
              onClick={handleGoBack}
              className="btn px-4 py-2 text-sm"
            >
              Go back
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
