import { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [task, setTask] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  async function register() {
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const response = await fetch('https://bootcamp2025.depster.me/registration', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();

    if(!response.ok) {
      const msg = data.message || 'An error occurred during registration.';
      setErrorMessage(msg);
      return;
    }
    setSuccessMessage('Successful registration! You can log in now.');
    }
    catch(err) {
    //console.log(err);
    setErrorMessage('Error in communication with the server.');
    }
  }

  async function login() {
    const response = await fetch('https://bootcamp2025.depster.me/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    const data = await response.json();
    setToken(data.data.token);
    console.log(data);
    alert('Login successful!');
  }

  async function getTask() {
    const response = await fetch('https://bootcamp2025.depster.me/task', {
      method: 'GET',
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();
    setTask(data);
    console.log(data);
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* LOGIN / REGISTRATION SECTION */}
      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
          Dept Bootcamp App
        </h1>

        {!token && (
        <div className="flex flex-col sm:flex-row gap-4 mb-6">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input-field"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input-field"
          />
        </div>)}

        <div className="flex gap-4">
          {!token && (
            <>
          <button onClick={register} className="btn">
            Register
          </button>
          <button onClick={login} className="btn">
            Login
          </button>
          </>)}

          {token && (
            <>
              <button onClick={getTask} className="btn">
                Get Task
              </button>
              <button className="btn">Task solution</button>
            </>
          )}
        </div>
      </div>

      {/* TASK SECTION (appears after login/getTask) */}
      {task && (
        <div className="p-6 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Task:</h2>
          <pre className="bg-white p-4 rounded shadow">
            {JSON.stringify(task, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;

