// src/components/CountryCard.jsx
import React, { useState } from 'react';
import { FiMapPin } from 'react-icons/fi';

export default function CountryCard({ country, onSave, onRemove, isSaved }) {
  const [isClicked, setIsClicked] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const handleToggleClick = () => {
    setIsClicked((prev) => !prev);
  };

  const handleMouseEnter = () => {
    if (!isClicked) {
      setIsHovered(true);
    }
  };

  const handleMouseLeave = () => {
    if (!isClicked) {
      setIsHovered(false);
    }
  };

  const showMap = isClicked || isHovered;

  return (
    <div
      className="card cursor-pointer"
      style={{ minHeight: '24rem' }}
      onClick={handleToggleClick}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {!showMap ? (
        <>
          {/* Flag image */}
          <div className="w-full h-48 overflow-hidden">
            <img
              src={country.flag}
              alt={`${country.name} flag`}
              className="w-full h-full object-cover"
            />
          </div>

          {/* Country info */}
          <div className="p-4 flex flex-col flex-grow">
            <h3 className="text-lg font-semibold text-gray-800 mb-2 text-center">
              {country.name}
            </h3>
            <div className="flex justify-center items-center text-gray-600 mb-4">
              <FiMapPin className="mr-1" />
              <span>{country.capital}</span>
            </div>
            <div className="mt-auto flex justify-center">
              {!isSaved ? (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onSave(country);
                  }}
                  className="btn px-4 py-2 text-sm"
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onRemove(country.code);
                  }}
                  className="btn bg-red-500 hover:bg-red-700 px-4 py-2 text-sm"
                >
                  Remove
                </button>
              )}
            </div>
          </div>
        </>
      ) : (
        <div className="w-full h-full flex flex-col">
          <iframe
            title={`map-${country.code}`}
            src={`https://maps.google.com/maps?q=${encodeURIComponent(
              country.name
            )}&output=embed`}
            className="w-full h-full"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
}
