// src/components/CountryCard.jsx
import React from 'react';

export default function CountryCard({ country, onSave, onRemove, isSaved }) {
  // country object should have: name, capital, flag (URL), code
  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      {/* Flag image */}
      <div className="w-full h-40 overflow-hidden">
        <img
          src={country.flag}
          alt={`${country.name} flag`}
          className="w-full h-full object-cover"
        />
      </div>

      {/* Country info */}
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{country.name}</h3>
        <p className="text-gray-600 mb-4">Capital: {country.capital}</p>
        {!isSaved ? (
          <button
            onClick={() => onSave(country)}
            className="btn px-4 py-2 text-sm"
          >
            Save
          </button>
        ) : (
          <button
            onClick={() => onRemove(country.code)}
            className="btn bg-red-500 hover:bg-red-700 px-4 py-2 text-sm"
          >
            Remove
          </button>
        )}
      </div>
    </div>
  );
}
