// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();               // ISO code iz URL-a
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState('');
  const [photos, setPhotos] = useState([]);    // niz URL-eva slika
  const [current, setCurrent] = useState(0);   // indeks carousel slike
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Ako nije logiran, vrati ga home
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // 2) Dohvati puni naziv države iz restcountries
  useEffect(() => {
    (async () => {
      try {
        const r = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const js = await r.json();
        if (Array.isArray(js) && js[0]?.name?.common) {
          setCountryName(js[0].name.common);
        } else {
          setCountryName(code);  // fallback
        }
      } catch {
        setCountryName(code);
      }
    })();
  }, [code]);

  // 3) Dohvati slike s Pixabay
  useEffect(() => {
    if (!countryName) return;

    (async () => {
      setLoading(true);
      setError('');

      const key = process.env.REACT_APP_PIXABAY_KEY;
      if (!key) {
        setError('Pixabay API key not set in .env!');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `https://pixabay.com/api/?key=${key}` +
            `&q=${encodeURIComponent(countryName)}` +
            `&image_type=photo&per_page=5`
        );
        if (!res.ok) throw new Error(`Pixabay ${res.status}`);
        const js = await res.json();
        if (Array.isArray(js.hits)) {
          setPhotos(js.hits.map((h) => h.webformatURL));
        } else {
          setError('Unexpected Pixabay response');
        }
      } catch (e) {
        setError(`Pixabay error: ${e.message}`);
      } finally {
        setLoading(false);
      }
    })();
  }, [countryName]);

  const prev = () =>
    setCurrent((c) => (photos.length ? (c > 0 ? c - 1 : photos.length - 1) : 0));
  const next = () =>
    setCurrent((c) => (photos.length ? (c < photos.length - 1 ? c + 1 : 0) : 0));

  // 4) Logout handler
  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // 5) Prikazi loading ili error
  if (loading && !error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading photos…</p>
      </div>
    );
  }
  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center px-4">
        <p className="text-red-600 mb-4">{error}</p>
        <button onClick={() => window.location.reload()} className="btn">
          Retry
        </button>
      </div>
    );
  }

  // 6) Glavni render
  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* 固定ナビ */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* spacer */}
      <div className="pt-16" />

      {/* Naslov države */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* Carousel */}
      <div className="mt-8 flex items-center justify-center space-x-4 px-4">
        {photos.length > 0 ? (
          <>
            <button onClick={prev} className="btn px-3 py-2 text-sm">
              ◀
            </button>
            <img
              src={photos[current]}
              alt={`${countryName} ${current + 1}`}
              className="w-full max-w-2xl h-64 object-cover rounded shadow"
            />
            <button onClick={next} className="btn px-3 py-2 text-sm">
              ▶
            </button>
          </>
        ) : (
          <p>No photos found for {countryName}.</p>
        )}
      </div>
    </div>
  );
}
