// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';
import CountryCard from './CountryCard';

export default function SolutionPage({
  token,
  message,
  setMessage,
  logout,
  countries,
  setCountries,
}) {
  const navigate = useNavigate();
  const [savedList, setSavedList] = useState([]);
  const [sortOrder, setSortOrder] = useState('');

  // 1) On mount: redirect if no token, else load saved
  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }
    const stored = JSON.parse(localStorage.getItem('savedCountries') || '[]');
    setSavedList(stored);
  }, [token, navigate]);

  // 2) Persist savedList
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(savedList));
  }, [savedList]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // Enrich basic country with capital, flag, latlng (omitted for brevity)
  async function enrichCountry(basic) { /* ... */ }

  // 3) Fetch countries, do NOT clear savedList
  const fetchCountries = async () => {
    setMessage({ type: '', text: '' });
    setCountries([]);
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      const basicList = Array.isArray(data)
        ? data
        : Array.isArray(data.data)
        ? data.data
        : [];
      const enriched = await Promise.all(
        basicList.map((basic) => enrichCountry(basic))
      );
      setCountries(enriched);
      setMessage({ type: 'success', text: 'Countries fetched successfully!' });
    }
  };

  // 4) Save / remove
  const saveCountry = (country) => {
    if (!savedList.some((c) => c.code === country.code)) {
      setSavedList((prev) => [...prev, country]);
    }
  };
  const removeCountry = (code) => {
    setSavedList((prev) => prev.filter((c) => c.code !== code));
  };
  const isCountrySaved = (code) => savedList.some((c) => c.code === code);

  // 5) Compute sorted list
  const sortedCountries = [...countries].sort((a, b) => {
    switch (sortOrder) {
      case 'area-asc':   return a.area - b.area;
      case 'area-desc':  return b.area - a.area;
      case 'pop-asc':    return a.population - b.population;
      case 'pop-desc':   return b.population - a.population;
      default:           return 0;
    }
  });

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Fixed Buttons Row */}
      <div className="fixed top-12 left-0 w-full bg-gray-100 z-10 flex items-end justify-center h-16">
        <div className="flex space-x-4 mb-2">
          <button onClick={fetchCountries} className="btn">
            Fetch Countries
          </button>
          <Link to="/saved">
            <button className="btn">Saved Countries</button>
          </Link>
          <Link to="/show-map">
            <button className="btn">Show Map</button>
          </Link>
        </div>
      </div>

      {/* Spacer */}
      <div className="pt-28" />

      {/* Message */}
      <div className="w-full flex justify-center mb-2">
        {message.text && (
          <div
            className={
              message.type === 'error'
                ? 'error-message'
                : 'success-message'
            }
          >
            {message.text}
          </div>
        )}
      </div>

      {/* Sort Dropdown */}
      {countries.length > 0 && (
        <div className="w-full max-w-7xl mx-auto px-2 mb-4">
          <label className="block mb-2 font-semibold text-gray-700">
            Sort by:
          </label>
          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="bg-white border border-gray-300 rounded px-3 py-2"
          >
            <option value="">— none —</option>
            <option value="area-asc">Area: small → large</option>
            <option value="area-desc">Area: large → small</option>
            <option value="pop-asc">Population: small → large</option>
            <option value="pop-desc">Population: large → small</option>
          </select>
        </div>
      )}

      {/* Cards Grid */}
      {sortedCountries.length > 0 && (
        <div className="w-full max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8 px-2">
          {sortedCountries.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              onSave={saveCountry}
              isSaved={isCountrySaved(country.code)}
              onRemove={removeCountry}
            />
          ))}
        </div>
      )}
    </div>
  );
}
