import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Random from './Random';

export default function TaskPage({ token, task, getTask, message, logout }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // split description into paragraphs…
  const paragraphs = (task?.data?.description || '')
    .split(/\n\s*\n/)
    .map(p => p.trim())
    .filter(p => p.length > 0);

  return (
    <div className="relative min-h-screen bg-gray-100">
      <Navbar backTo="/" token={token} logout={logout} />

      {/* Spacer */}
      <div className="pt-16" />

      {/* Show Task button */}
      <div className="w-full flex flex-col items-center space-y-6 mb-6 px-4">
        <button onClick={getTask} className="btn">
          Show Task
        </button>

        {/* ===== Experiment: show raw random-countries format ===== */}
        <Random token={token} limit={10} />
      </div>

      {task && (
        <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-6">
            {task.data.name}
          </h1>
          <div className="space-y-4 text-gray-700">
            {paragraphs.map((para, idx) => (
              <p key={idx}>
                {para.split(/\n/).map((line, i) => (
                  <span key={i}>
                    {line}
                    {i < para.split(/\n/).length - 1 && <br />}
                  </span>
                ))}
              </p>
            ))}
          </div>
        </div>
      )}

      {message.text && (
        <div className="w-full flex justify-center mb-2">
          <div
            className={
              message.type === 'error' ? 'error-message' : 'success-message'
            }
          >
            {message.text}
          </div>
        </div>
      )}
    </div>
  );
}
