// src/components/TaskPage.jsx
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackToHomeButton';

export default function TaskPage({ token, task, getTask, message, logout }) {
  const navigate = useNavigate();

  // If not logged in, redirect back to home
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-10 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackToHomeButton />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer to keep content below fixed bar */}
      <div className="pt-16" />

      {/* Show any message */}
      {message.text && (
        <div className="w-full flex justify-center mb-4">
          <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
            {message.text}
          </div>
        </div>
      )}

      {/* Fetch Task button (same vertical placement as before) */}
      <div className="w-full flex justify-center mb-6">
        <button onClick={getTask} className="btn">
          Show Task
        </button>
      </div>

      {/* Display task JSON */}
      {task && (
        <div className="p-6 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Task:</h2>
          <pre className="bg-white p-4 rounded shadow">
            {JSON.stringify(task, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}
