// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';
import CountryCard from './CountryCard';

export default function SolutionPage({
  token,
  message,
  setMessage,
  logout,
  countries,
  setCountries,
}) {
  const navigate = useNavigate();
  const [savedList, setSavedList] = useState([]);
  const [sortOrder, setSortOrder] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Redirect + load saved
  useEffect(() => {
    if (!token) return navigate('/');
    const stored = JSON.parse(localStorage.getItem('savedCountries') || '[]');
    setSavedList(stored);
  }, [token, navigate]);

  // Persist saved
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(savedList));
  }, [savedList]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // enrichCountry as beforeâ€¦
  async function enrichCountry(basic) {
    let capital = 'N/A', flag = '', latlng = [0, 0];
    try {
      const rc = await fetch(`https://restcountries.com/v3.1/alpha/${basic.code}`);
      const rcData = await rc.json();
      if (rcData[0]) {
        const info = rcData[0];
        capital = Array.isArray(info.capital) ? info.capital[0] : info.capital || 'N/A';
        if (Array.isArray(info.latlng)) latlng = info.latlng;
      }
    } catch {}
    try {
      const cn = await fetch(
        'https://countriesnow.space/api/v0.1/countries/flag/images',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ iso2: basic.code }),
        }
      );
      const cnData = await cn.json();
      if (cnData.data?.flag) flag = cnData.data.flag;
    } catch {}
    return {
      code: basic.code,
      name: basic.name,
      capital,
      flag,
      area: basic.area || 0,
      population: basic.population || 0,
      latlng,
    };
  }

  const fetchCountries = async () => {
    setMessage({ type: '', text: '' });
    setCountries([]);
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      const basicList = Array.isArray(data)
        ? data
        : Array.isArray(data.data)
        ? data.data
        : [];
      const enriched = await Promise.all(basicList.map(enrichCountry));
      setCountries(enriched);
      setMessage({ type: 'success', text: 'Countries fetched successfully!' });
    }
  };

  const saveCountry = (c) => {
    if (!savedList.find((x) => x.code === c.code)) {
      setSavedList((prev) => [...prev, c]);
    }
  };
  const removeCountry = (code) => {
    setSavedList((prev) => prev.filter((x) => x.code !== code));
  };
  const isCountrySaved = (code) => savedList.some((x) => x.code === code);

  // filter + sort
  const visible = countries
    .filter((c) =>
      c.name.toLowerCase().includes(searchTerm.trim().toLowerCase())
    )
    .sort((a, b) => {
      if (sortOrder === 'area-asc') return a.area - b.area;
      if (sortOrder === 'area-desc') return b.area - a.area;
      if (sortOrder === 'pop-asc') return a.population - b.population;
      if (sortOrder === 'pop-desc') return b.population - a.population;
      return 0;
    });

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Fixed Buttons Row */}
      <div className="fixed top-12 left-0 w-full bg-gray-100 z-10 flex items-end justify-center h-16">
        <div className="flex space-x-4 mb-2">
          <button onClick={fetchCountries} className="btn">
            Fetch Countries
          </button>
          <Link to="/saved">
            <button className="btn">Saved Countries</button>
          </Link>
          <Link to="/show-map">
            <button className="btn">Show Map</button>
          </Link>
        </div>
      </div>

      {/* Spacer under fixed bars */}
      <div className="pt-24" />

      {/* Message */}
      <div className="w-full flex justify-center mb-2">
        {message.text && (
          <div
            className={
              message.type === 'error'
                ? 'error-message'
                : 'success-message'
            }
          >
            {message.text}
          </div>
        )}
      </div>

      {/* Search + Sort (always visible once you've fetched) */}
      {countries.length > 0 && (
        <div className="w-full max-w-7xl mx-auto px-2 mb-4 flex flex-wrap items-center gap-4">
          <input
            type="text"
            placeholder="Search fetched countries..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="bg-white border border-gray-300 rounded px-3 py-2 flex-1 min-w-[200px]"
          />
          <div>
            <label className="block mb-1 font-semibold text-gray-700">
              Sort by:
            </label>
            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value)}
              className="bg-white border border-gray-300 rounded px-3 py-2"
            >
              <option value="">â€” none â€”</option>
              <option value="area-asc">Area: small â†’ large</option>
              <option value="area-desc">Area: large â†’ small</option>
              <option value="pop-asc">Population: small â†’ large</option>
              <option value="pop-desc">Population: large â†’ small</option>
            </select>
          </div>
        </div>
      )}

      {/* Country Cards Grid */}
      {visible.length > 0 && (
        <div className="w-full max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8 px-2">
          {visible.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              onSave={saveCountry}
              isSaved={isCountrySaved(country.code)}
              onRemove={removeCountry}
            />
          ))}
        </div>
      )}
    </div>
  );
}
