// src/components/SolutionPage.jsx
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../images/dept-logo.png';

export default function SolutionPage({ token, message, logout }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="relative flex flex-col items-center justify-center min-h-screen px-4">
        <img
         src={logo}
         alt="Dept Logo"
         className="absolute top-4 left-4 w-20 h-auto"
        />

      {token && (
        <button
          onClick={handleLogout}
          className="btn absolute top-4 right-4"
        >
          Log out
        </button>
      )}

      <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
        Dept Bootcamp App
      </h1>

      {message.text && (
        <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
          {message.text}
        </div>
      )}

      <div className="text-gray-700">
        <p className="mb-4">This is where the “Show Solution” UI will appear.</p>
      </div>
    </div>
  );
}
