// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';
import CountryCard from './CountryCard';

export default function SolutionPage({ token, message, setMessage, logout }) {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);       // enriched country data
  const [saved, setSaved] = useState([]);               // list of saved country objects

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  // Load saved countries from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem('savedCountries');
    if (stored) {
      setSaved(JSON.parse(stored));
    }
  }, []);

  // Persist saved list to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(saved));
  }, [saved]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // Helper: fetch capital from restcountries and flag from countriesnow
  const enrichCountry = async (basic) => {
    let capital = 'N/A';
    let flag = '';

    // Fetch capital from restcountries by ISO2 code
    try {
      const rcResponse = await fetch(
        `https://restcountries.com/v3.1/alpha/${basic.code}`
      );
      const rcData = await rcResponse.json();
      if (Array.isArray(rcData) && rcData.length > 0) {
        const info = rcData[0];
        capital = Array.isArray(info.capital) ? info.capital[0] : info.capital || 'N/A';
      }
    } catch (err) {
      console.error(`Failed to fetch capital for ${basic.name}:`, err);
    }

    // Fetch flag URL from countriesnow API
    try {
      const cnResponse = await fetch(
        'https://countriesnow.space/api/v0.1/countries/flag/images',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ iso2: basic.code }),
        }
      );
      const cnData = await cnResponse.json();
      if (cnData.data && typeof cnData.data.flag === 'string') {
        flag = cnData.data.flag;
      }
    } catch (err) {
      console.error(`Failed to fetch flag for ${basic.name}:`, err);
    }

    return {
      code: basic.code,
      name: basic.name,
      capital,
      flag,
    };
  };

  // Fetch random countries using doRequest and then enrich each
  const fetchCountries = async () => {
    setMessage({ type: '', text: '' });
    setCountries([]);

    // Step 1: fetch basic list from bootcamp API
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      const basicList = Array.isArray(data)
        ? data
        : Array.isArray(data.data)
        ? data.data
        : [];
      // Step 2: enrich each country with flag and capital
      const enriched = await Promise.all(
        basicList.map((basic) => enrichCountry(basic))
      );
      setCountries(enriched);
    }
  };

  // Save a country to the list (avoid duplicates)
  const saveCountry = (country) => {
    if (!saved.find((c) => c.code === country.code)) {
      setSaved([...saved, country]);
    }
  };

  // Remove a country from the list
  const removeCountry = (code) => {
    setSaved(saved.filter((c) => c.code !== code));
  };

  return (
    <div className="relative flex flex-col items-center justify-center min-h-screen px-4">
      {/* Logo and Back button */}
      <div className="absolute top-4 left-4 flex items-center space-x-2">
        <img src={logo} alt="Dept Logo" className="w-20 h-auto" />
        <BackToHomeButton />
      </div>

      {/* Logout button */}
      {token && (
        <button onClick={handleLogout} className="btn absolute top-4 right-4">
          Log out
        </button>
      )}

      <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
        Dept Bootcamp App
      </h1>

      {/* Show any message */}
      {message.text && (
        <div
          className={
            message.type === 'error' ? 'error-message' : 'success-message'
          }
        >
          {message.text}
        </div>
      )}

      {/* Fetch button */}
      <button onClick={fetchCountries} className="btn mb-6">
        Fetch Countries
      </button>

      {/* Display fetched countries in cards (4 columns on large screens) */}
      {countries.length > 0 && (
        <div className="w-full max-w-7xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8">
          {countries.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              onSave={saveCountry}
              isSaved={!!saved.find((c) => c.code === country.code)}
              onRemove={removeCountry}
            />
          ))}
        </div>
      )}

      {/* Display saved list in cards */}
      {saved.length > 0 && (
        <div className="w-full max-w-7xl">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">
            Saved Countries:
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {saved.map((country) => (
              <CountryCard
                key={country.code}
                country={country}
                isSaved={true}
                onRemove={removeCountry}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
