// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackToHomeButton from './BackToHomeButton';

export default function SolutionPage({ token, message, logout, setMessage }) {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  const [saved, setSaved] = useState([]);

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [token, navigate]);

  // Load saved countries from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem('savedCountries');
    if (stored) {
      setSaved(JSON.parse(stored));
    }
  }, []);

  // Persist saved list to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(saved));
  }, [saved]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  // Fetch random countries using doRequest
  const fetchCountries = async () => {
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      setCountries(data);
    }
  };

  // Save a country to the list (avoid duplicates)
  const saveCountry = (country) => {
    if (!saved.find((c) => c.code === country.code)) {
      setSaved([...saved, country]);
    }
  };

  // Remove a country from the list
  const removeCountry = (code) => {
    setSaved(saved.filter((c) => c.code !== code));
  };

  return (
    <div className="relative flex flex-col items-center justify-center min-h-screen px-4">
      {/* Logo and Back button */}
      <div className="absolute top-4 left-4 flex items-center space-x-2">
        <img src={logo} alt="Dept Logo" className="w-20 h-auto" />
        <BackToHomeButton />
      </div>

      {/* Logout button */}
      {token && (
        <button onClick={handleLogout} className="btn absolute top-4 right-4">
          Log out
        </button>
      )}

      <h1 className="text-6xl font-bold text-gray-800 mb-8 text-center">
        Dept Bootcamp App
      </h1>

      {/* Show any message */}
      {message.text && (
        <div className={message.type === 'error' ? 'error-message' : 'success-message'}>
          {message.text}
        </div>
      )}

      {/* Fetch button */}
      <button onClick={fetchCountries} className="btn mb-6">
        Fetch Countries
      </button>

      {/* Display fetched countries with Save option */}
      {countries.length > 0 && (
        <div className="w-full max-w-3xl mb-8">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Random Countries:</h2>
          <ul className="space-y-4">
            {countries.map((country) => (
              <li
                key={country.code}
                className="flex justify-between items-center bg-white p-4 rounded shadow"
              >
                <span>{country.name}</span>
                <button
                  onClick={() => saveCountry(country)}
                  className="btn px-3 py-1 text-sm"
                >
                  Save
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Display saved list with Remove option */}
      {saved.length > 0 && (
        <div className="w-full max-w-3xl">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Saved Countries:</h2>
          <ul className="space-y-4">
            {saved.map((country) => (
              <li
                key={country.code}
                className="flex justify-between items-center bg-white p-4 rounded shadow"
              >
                <span>{country.name}</span>
                <button
                  onClick={() => removeCountry(country.code)}
                  className="btn bg-red-500 hover:bg-red-700 px-3 py-1 text-sm"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
