// src/components/CountryDetailPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';

export default function CountryDetailPage({ token, logout }) {
  const { code } = useParams();
  const navigate = useNavigate();

  const [countryName, setCountryName] = useState(code);
  const [photos, setPhotos] = useState([]);
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(true);

  // Redirect ako nije prijavljen
  useEffect(() => {
    if (!token) navigate('/');
  }, [token, navigate]);

  // Fetch fullname
  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(`https://restcountries.com/v3.1/alpha/${code}`);
        const data = await res.json();
        if (Array.isArray(data) && data[0]?.name?.common) {
          setCountryName(data[0].name.common);
        }
      } catch {}
    })();
  }, [code]);

  // Fetch Unsplash photos (koristeći Authorization header)
  useEffect(() => {
    if (!countryName) return;
    (async () => {
      setLoading(true);
      try {
        const accessKey = process.env.REACT_APP_UNSPLASH_ACCESS_KEY;
        const res = await fetch(
          `https://api.unsplash.com/search/photos?query=${encodeURIComponent(
            countryName
          )}&per_page=5`,
          {
            headers: {
              Authorization: `Client-ID ${accessKey}`
            }
          }
        );
        const json = await res.json();
        if (Array.isArray(json.results)) {
          setPhotos(json.results.map((r) => r.urls.regular));
        }
      } catch (err) {
        console.error('Error fetching photos:', err);
      } finally {
        setLoading(false);
      }
    })();
  }, [countryName]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading && photos.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p>Loading photos…</p>
      </div>
    );
  }

  const prev = () => setCurrent((c) => (c > 0 ? c - 1 : photos.length - 1));
  const next = () => setCurrent((c) => (c < photos.length - 1 ? c + 1 : 0));

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/solution" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Spacer */}
      <div className="pt-16" />

      {/* Title */}
      <h1 className="text-8xl font-bold text-gray-800 text-center mt-6">
        {countryName}
      </h1>

      {/* Carousel ili fallback */}
      <div className="mt-8 flex items-center justify-center space-x-4">
        {photos.length > 0 ? (
          <>
            <button onClick={prev} className="btn px-3 py-2 text-sm">◀</button>
            <img
              src={photos[current]}
              alt={`${countryName} ${current + 1}`}
              className="w-full max-w-2xl h-64 object-cover rounded shadow"
            />
            <button onClick={next} className="btn px-3 py-2 text-sm">▶</button>
          </>
        ) : (
          <p>No photos found for {countryName}.</p>
        )}
      </div>
    </div>
  );
}
