// src/components/SolutionPage.jsx
import { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { doRequest } from '../utils/api';
import logo from '../images/dept-logo.png';
import BackButton from './BackButton';
import CountryCard from './CountryCard';

export default function SolutionPage({
  token,
  message,
  setMessage,
  logout,
  countries,
  setCountries,
}) {
  const navigate = useNavigate();
  const [savedList, setSavedList] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // 1) On mount: if no token, redirect; otherwise load savedList
  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }
    const stored = JSON.parse(localStorage.getItem('savedCountries') || '[]');
    setSavedList(stored);
  }, [token, navigate]);

  // 2) Whenever savedList changes, persist to localStorage
  useEffect(() => {
    localStorage.setItem('savedCountries', JSON.stringify(savedList));
  }, [savedList]);

  const handleLogout = () => {
    logout();
    navigate('/');
    // leave localStorage intact
  };

  // enrichCountry omitted for brevity, same as before...
  const enrichCountry = async (basic) => { /* ... */ };
  const fetchCountries = async () => {
    setMessage({ type: '', text: '' });
    setCountries([]);
    setSearchTerm('');
    const data = await doRequest({
      url: 'https://bootcamp2025.depster.me/api/countries',
      method: 'GET',
      body: null,
      token,
      setMessage,
    });
    if (data) {
      const basicList = Array.isArray(data)
        ? data
        : Array.isArray(data.data)
        ? data.data
        : [];
      const enriched = await Promise.all(
        basicList.map((basic) => enrichCountry(basic))
      );
      setCountries(enriched);
      setMessage({ type: 'success', text: 'Countries fetched successfully!' });
    }
  };
  const saveCountry = (country) => {
    if (!savedList.some((c) => c.code === country.code)) {
      setSavedList((prev) => [...prev, country]);
    }
  };
  const removeCountry = (code) => {
    setSavedList((prev) => prev.filter((c) => c.code !== code));
  };
  const isCountrySaved = (code) => savedList.some((c) => c.code === code);

  // filter by searchTerm
  const filtered = countries.filter((c) =>
    c.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Fixed Top Bar */}
      <div className="fixed top-0 left-0 w-full bg-gray-100 z-20 flex items-center justify-between px-4 py-2 shadow-none">
        <div className="flex items-center space-x-2">
          <img src={logo} alt="Dept Logo" className="w-12 h-auto" />
          <BackButton to="/" />
        </div>
        {token && (
          <button onClick={handleLogout} className="btn">
            Log out
          </button>
        )}
      </div>

      {/* Fixed button row */}
      <div className="fixed top-10 left-0 w-full bg-gray-100 z-10 flex items-end justify-center h-16">
        <div className="flex space-x-4 mb-2">
          <button onClick={fetchCountries} className="btn">
            Fetch Countries
          </button>
          <Link to="/saved">
            <button className="btn">Saved Countries</button>
          </Link>
          <Link to="/show-map">
            <button className="btn">Show Map</button>
          </Link>
        </div>
      </div>

      {/* Spacer */}
      <div className="pt-24" />

      {/* Message */}
      <div className="w-full flex justify-center mb-2">
        {message.text && (
          <div
            className={message.type === 'error' ? 'error-message' : 'success-message'}
          >
            {message.text}
          </div>
        )}
      </div>

      {/* Search input - aligned with cards */}
      {countries.length > 0 && (
        <div className="w-full max-w-7xl mx-auto px-2 mb-6">
          <input
            type="text"
            placeholder="Search fetched countries..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="input-field w-64"
          />
        </div>
      )}

      {/* Country cards grid */}
      {filtered.length > 0 && (
        <div className="w-full max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8 px-2">
          {filtered.map((country) => (
            <CountryCard
              key={country.code}
              country={country}
              onSave={saveCountry}
              isSaved={isCountrySaved(country.code)}
              onRemove={removeCountry}
            />
          ))}
        </div>
      )}

      {/* No match message */}
      {countries.length > 0 && filtered.length === 0 && (
        <p className="text-center text-gray-600">No countries match your search.</p>
      )}
    </div>
  );
}
